mapper.insert:
INSERT INTO plant_master (
    plant_user_seq
    , plant_etc
    , plant_nickname
    , plant_odata_seq
)
VALUES (
    #{userSeq}
    , #{etc}
    , #{plantNickName}
    , #{odataSeq}
)

mapper.update:
UPDATE plant_master
SET plant_nickname = #{plantNickName}
, plant_etc = #{etc}
WHERE plant_user_seq = #{userSeq}
  AND plant_seq = #{plantSeq}

mapper.delete:
DELETE FROM plant_master
WHERE plant_user_seq = #{userSeq}
  AND plant_seq = #{plantSeq}
